			<br.com.dina.ui.widget.UIButton
				android:layout_width="fill_parent"  
				android:layout_height="fill_parent"
				ui:title="some title two"
				ui:subtitle="some subtitle two"
				android:padding="10dip" />	

			<br.com.dina.ui.widget.UIButton
				android:layout_width="fill_parent"  
